  v_mod4 INTEGER;
  v_mod5 INTEGER;
  v_mod6 INTEGER;
BEGIN

-- Get the course ID
SELECT id INTO v_course_id FROM courses WHERE slug = 'undivided-interest-property-ownership';

IF v_course_id IS NULL THEN
