-- MODULE 2: Tenancy in Common — Deep Dive
-- =============================================
INSERT INTO course_modules (title, section, description, tier, sort_order, course_id)
VALUES (
  'Tenancy in Common — Deep Dive',
  'Understanding Shared Ownership',
  'Explore the most common form of co-ownership in detail. Learn how TIC agreements work, how to handle unequal ownership shares, and the practical challenges agents face with TIC transactions.',
  'free',
  2,
