INSERT INTO course_quizzes (module_id, question, options, correct_index, sort_order) VALUES
(v_mod3, 'What is the primary reason buyers purchase short sale properties?', '["The properties are always in perfect condition", "Value — short sales often sell below comparable market prices", "Short sales close faster than traditional sales", "There is no competition from other buyers"]', 1, 1),
(v_mod3, 'What is a pre-approved short sale?', '["A sale where the buyer is pre-approved for a mortgage", "A sale where the lender has already reviewed the seller''s financials and established an acceptable price", "A sale that has been approved by the local government", "A sale where the listing agent has pre-approved the buyer"]', 1, 2),
(v_mod3, 'When should a short sale buyer lock their mortgage rate?', '["At the time of the offer", "When the lender approval is imminent or received", "Six months before the expected closing date", "Rate locks are not needed for short sales"]', 1, 3),
(v_mod3, 'What should a buyer''s agent do during the lender review waiting period?', '["Stop communicating until there is news", "Check in with the listing agent weekly and update the buyer regularly", "Tell the buyer to stop looking at other properties", "Contact the lender directly without the listing agent"]', 1, 4),
(v_mod3, 'What makes a short sale offer most attractive to the lender?', '["The lowest possible price", "Maximum seller concessions", "High net proceeds to the lender, strong financing, and certainty of closing", "The longest possible closing timeline"]', 2, 5);


